<script>
import { gsap, Bounce, Power2, Power1 } from "gsap";
export default {
  props: ["colour"],

  mounted() {
    this.float();
  },
  methods: {
    float() {
      const tl = gsap.timeline();
      tl.to("#RGhost", 2, {
        y: "-=5",
        ease: Power1.easeInOut,
      })
        .to("#RGhost", 1, {
          y: "+=5",
          ease: Power1.easeInOut,
        })
        .to(
          ".r-eyes",
          0.05,
          {
            scaleX: 1,
            scaleY: 0,
            transformOrigin: "bottom",
            repeat: 1,
            yoyo: true,
            ease: Power2.in,
            delay: 2,
          },
          1
        );

      return tl
        .repeat(-1)
        .duration(5)
        .yoyo()
        .play();
    },
  },
};
</script>
